SET NAMES UTF8;
DROP DATABASE IF EXISTS guitar;
CREATE DATABASE guitar CHARSET=UTF8;
USE guitar;

/*用户表*/
CREATE TABLE mf_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,/*用户编号*/
    uname VARCHAR(32),/*用户名*/
    upwd VARCHAR(32),/*密码*/
    phone VARCHAR(11)/*手机号*/
);

/*新闻表*/
CREATE TABLE mf_news(
    nid INT PRIMARY KEY AUTO_INCREMENT,/*新闻id*/
    title   VARCHAR(64),    /*标题*/
    pubTime BIGINT,         /*发布数据*/
    content VARCHAR(2048)   /*内容*/
);
/*产品表*/
CREATE TABLE mf_product(
    pid INT PRIMARY KEY AUTO_INCREMENT,/*产品id*/
    title1 VARCHAR(64), /*一级标题*/
    title2 VARCHAR(64), /*二级标题*/
    pic VARCHAR(64),    /*主图片*/
    type INT,           /*类型：1-木吉他，2-电吉他*/
    model VARCHAR(64),  /*型号*/
    size VARCHAR(64),    /*吉他尺寸*/
    sort VARCHAR(64),  /*箱体材质分类*/
    mmaterial VARCHAR(64),   /*面板材质*/
    zmaterial VARCHAR(64),/*指板材质*/
    bmaterial VARCHAR(64),   /*背侧板材质*/
    grover VARCHAR(64),  /*卷弦器*/
    price FLOAT(10,2),   /*价格*/
    detail VARCHAR(2048) /*详情*/
);



/*购物车表*/
CREATE TABLE mf_cart(
    cid INT PRIMARY KEY AUTO_INCREMENT,/*购物车id*/
    userId INT /*用户id*/
);

/*购物车详情表*/
CREATE TABLE mf_cart_detail(
    did INT PRIMARY KEY AUTO_INCREMENT,
    cartId  INT,    /*购物车id*/
    productId INT,  /*产品id*/
    count INT       /*数量*/
);






